import React from 'react';
import { NavLink } from 'react-router-dom';
import './PopupWithForm.css';

function PopupWithForm({ title, name, submitText, onClose, onSubmit, errorForm, inputValues, link, linkText, children }) {
    return (
        <div className="popup popup_opened">
            <form className="popup__container container" onSubmit={onSubmit} name={name} action="#" noValidate>
                  <button className="popup__close-button" onClick={onClose} type="button" />
                  <h2 className="popup__title">{title}</h2>
                  {children}
                  <button 
                  className={`popup__submit-button popup__${name}_type_submit-button ${!(inputValues && !errorForm) ? "popup__submit-button_disabled" : ""}`} 
                  type="submit">
                      <span className="popup__form-error">Такой пользователь уже есть</span>
                      {submitText}
                  </button>
                  <p className="popup__nav">или <NavLink className="popup__link" to={link}>{linkText}</NavLink></p>
              </form>
        </div>
    )
}

export default PopupWithForm;
