import React from 'react';
import './Login.css';
import PopupWithForm from '../PopupWithForm/PopupWithForm.js'

function Login({ isOpen, onClose, onLogin, isLoading }) {

    const [email, setEmail] = React.useState('');
    const [password, setPassword] = React.useState('');
    const [errorEmail, setErrorEmail] = React.useState({});
    const [errorPassword, setErrorPassword] = React.useState({});

    const errorForm = errorEmail.error || errorPassword.error;

    function handleSubmit(e) {
        e.preventDefault();
        onLogin({ email, password });
    }

    return (
        <PopupWithForm
            title="Вход"
            name="login"
            submitText="Войти"
            onClose={onClose}
            onSubmit={handleSubmit}
            errorForm={errorForm}
            inputValues={email && password}
        >
        <label className="popup__field">
            <span className="popup__input-caption">Email</span>
            <input 
            className={`popup__input popup__input_type_name ${errorEmail.error ? "popup__input_novalid" : ""}`}
            type="email" 
            id="loginEmail"
            value={email}
            onChange={(evt) => setEmail(evt.target.value)}
            name="loginEmail" 
            placeholder="Введите почту" 
            minLength="2" 
            maxLength="50" 
            required 
            />
            <span className={`popup__input-error ${errorEmail.error ? "popup__input-error_active" : ""}`} id="name-error">
                {errorEmail.errorText}
            </span>
        </label>
        <label className="popup__field">
            <span className="popup__input-caption">Пароль</span>
            <input 
            className={`popup__input popup__input_type_job ${errorPassword.error ? "popup__input_novalid" : ""}`}
            type="password" 
            id="loginPass"
            value={password}
            onChange={(evt) => setPassword(evt.target.value)}
            name="loginPass" 
            placeholder="Введите пароль"
            minLength="6"
            maxLength="50"
            required
            />
            <span className={`popup__input-error ${errorPassword.error ? "popup__input-error_active" : ""}`} id="job-error">
                {errorPassword.errorText}
            </span>
        </label>
        </PopupWithForm>
    )
}

export default Login;
