import React from 'react';
import './Navigation.css';
import { NavLink } from 'react-router-dom';
import logoutWhite from '../../images/logout-white.png';
import logout from '../../images/logout-black.png';

function Navigation({ loggedIn, pathname }) {
    return (
        <>
            <ul className="nav__container">
                <li className={`nav__item ${pathname === "/" ? "nav__item_type_white nav__item_active" : ""}`}>
                    <NavLink className={`nav__link ${pathname === "/" ? "nav__link_type_white" : ""}`} exact to="/">Главная</NavLink>
                </li>
                {loggedIn
                    ?
                    <>
                        <li className={`nav__item ${pathname === "/" ? "nav__item_type_white" : ""} ${pathname === "/saved-news" ? "nav__item_active" : ""}`}>
                            <NavLink className={`nav__link ${pathname === "/" ? "nav__link_type_white" : ""}`} to="/saved-news">Сохранённые статьи</NavLink>
                        </li>
                        <li className="nav__item">
                            <button 
                            className={`nav__btn ${pathname === "/" ? "nav__btn_type_white" : ""}`}>
                            Грета&nbsp;
                            <img className="logout-icon" src={`${pathname === "/" ? logoutWhite : logout}`} alt="Выход" />
                            </button>
                        </li>
                    </>
                    :
                    <li className="nav__item">
                        <button className={`nav__btn ${pathname === "/" ? "nav__btn_type_white" : ""}`}>Авторизоваться</button>
                    </li>
                }
            </ul>
        </>
    )
}

export default Navigation;
